import React, {Component} from 'react';
import {Navigate} from '../App';
import {
    Divider,
    Typography,
    Button,
    List,
    ListItem,
    ListItemText,
    LinearProgress
} from '@material-ui/core';
import logo from '../goodness.png';
import {LabelledValue} from './LabelledValue';

interface HomeScreenProps {
    navigate: Navigate;
    challenges: string[];
}

interface HomeScreenState {}

export class HomeScreen extends Component<HomeScreenProps, HomeScreenState> {
    constructor(props: HomeScreenProps) {
        super(props);
        this.state = {};
    }

    render() {
        return (
            <div style={{padding: '12px', display: 'flex', flex: 1, flexDirection: 'column'}}>
                <div
                    style={{
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        padding: '2ch'
                    }}
                >
                    <img src={logo} style={{width: '20ch'}} alt='logo'/>
                    <Button onClick={() => this.props.navigate('DailyCheckIn')} variant='outlined'>
                        Check In
                    </Button>
                </div>
                <Typography variant='h5'>Today's Challenges</Typography>
                <List dense>
                    {this.props.challenges.map(challenge => (
                        <ListItem button style={{borderRadius: '4px'}}>
                            <ListItemText>{challenge}</ListItemText>
                        </ListItem>
                    ))}
                </List>
                <Divider/>
                <Typography variant='h5' style={{margin: '8px 0'}}>Progress Overview</Typography>
                <LinearProgress variant='determinate' value={50} style={{height: '0.5em'}}/>
                <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-around', margin: '8px 0'}}>
                  <LabelledValue label='Nutrition' value='75%'/>
                  <LabelledValue label='Exercise' value='50%'/>
                  <LabelledValue label='Challenges Completed' value='18/30'/>
                </div>
            </div>
        );
    }
}
