import React, {Component} from 'react';
import {Navigate} from '../App';
import {
    Divider,
    Typography,
    Button,
    List,
    ListItem,
    ListItemText,
    LinearProgress
} from '@material-ui/core';
import logo from '../goodness.png';
import bg from '../homescreen.jpg';
import x from '../x.jpg';
import {LabelledValue} from './LabelledValue';

interface HomeScreenProps {
    navigate: Navigate;
    challenges: string[];
}

interface HomeScreenState {}

export class HomeScreen extends Component<HomeScreenProps, HomeScreenState> {
    constructor(props: HomeScreenProps) {
        super(props);
        this.state = {};
    }

    render() {
        return (
            <div style={{padding: '12px', display: 'flex', flex: 1, flexDirection: 'column', justifyContent: 'space-between', backgroundImage: `url(${x})`, backgroundSize: 'cover'}}>
                <div
                    style={{
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        padding: '2ch'
                    }}
                >
                    <img src={logo} style={{width: '20ch'}} alt='logo'/>
                    <Button onClick={() => this.props.navigate('DailyCheckIn')} variant='outlined'>
                        Check In
                    </Button>
                </div>
                <Typography variant='h4'>Today's Challenges</Typography>
                <List>
                    {this.props.challenges.map(challenge => (
                        <ListItem button style={{borderRadius: '4px'}}>
                            <ListItemText>{challenge}</ListItemText>
                        </ListItem>
                    ))}
                </List>
                <Divider/>
                <Typography variant='h4'>Progress Overview</Typography>
                <Typography variant='h6'>Challenge Completion</Typography>
                <LinearProgress variant='determinate' value={50} style={{height: '0.5em'}}/>
                <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-around'}}>
                  <LabelledValue label='Diet' value='75%'/>
                  <LabelledValue label='Exercise' value='50%'/>
                  <LabelledValue label='Social' value='40%'/>
                </div>
                <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-around'}}>
                  <LabelledValue label='Social' value='75%'/>
                  <LabelledValue label='Career' value='50%'/>
                  <LabelledValue label='Challenges' value='18/30'/>
                </div>
            </div>
        );
    }
}
